mapper.select:
SELECT DISTINCT(s.accnt_code) as "value",s.accnt_name as "text"
FROM reimburse_base_biztype_accnt_item_rela s
WHERE s.cost_type_code = #{costType} AND s.COSTCENTER_TYPE=#{costcenterType}
AND s.biz_type_code = #{bizTypeCode}
AND s.fee_item_code = #{feeItemCode}
AND s.enable_flag='Y'
AND #{now} >= s.START_VALID_TIME
AND #{now} <= s.END_VALID_TIME
ORDER by s.accnt_code asc

SELECT s.item_text FROM sys_dict_item s 
WHERE s.dict_id = (SELECT id FROM sys_dict WHERE dict_code = #{code})
AND s.item_value = #{key}
